targets.query:
lookup = {Today: {p: -1d, f: "today"}, Month: {p: -32d, f: "month"}, Year: {p: -13mo, f: "year"}}
from(bucket: "multisma2")
  |> range(start: lookup.${period}.p)
  |> filter(fn: (r) => r._measurement == "production" and r._inverter == "site" and r._field == lookup.${period}.f)
  |> map(fn: (r) => ({ _time: r._time, _kwh: (float(v: r._value) * 1.0) }))

